var _home = _interopRequireDefault(require("../pages/home.page"));
var _busqueda = _interopRequireDefault(require("../pages/busqueda.page"));
var _color = _interopRequireDefault(require("../pages/color.page"));
var _articulos = _interopRequireDefault(require("../datos/articulos"));
function _interopRequireDefault(obj) { return obj && obj.__esModule ? obj : { default: obj }; }
describe('Búsqueda', () => {
  _articulos.default.forEach(({
    articulo
  }) => {
    it(`Debería buscar ${articulo}`, async () => {
      await _home.default.abrir('/');
      await _home.default.buscar(articulo);
      expect(await _home.default.obtenerTextoBusqueda()).to.equal(articulo);
      expect(await _busqueda.default.obtenerNombreResultado()).to.equal(articulo);
    });
  });
});
it('Comparación de imágenes con página de WebdriverIO', async () => {
  await browser.url('https://webdriver.io');
});
